package day043;

public class Logic_operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean a=true;
		boolean b=false;
		boolean c=!b; //ㅊ 변수가 B 가 투르라면 부정, 부정이라면 긍정
		
		System.out.println(a);
		System.out.println(!a);
		
		System.out.println(b);
		System.out.println(c);
	}

}
