package day043;

public class Operator_ch3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=1;
		System.out.println(a);
		
		a++; //a값 증가 후위형
		System.out.println(a); //a++ 로 인해 1이 증가
		System.out.println(++a); // 1증가 전위형 : 증가가 우선 출형
		System.out.println(a++); // 1증가 후위형 : 증가가 나중에 출력
		System.out.println(a); // 위 증가 상승값음 형재 값에서 출혁한 것.
	}

}
