package day049;

public class While {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
//		while(조건식) {반복할 명령}
		//x : 1, ~ ,10 으로 변경이 된다는 명령을 반복하기 위함
		
		
		int x=1;
		// ("x"+x) x++ 를 간단하게 할 예정
		
		while(x<=10) { //참일 결우까지 계속해서 반복함
					System.out.println("x:"+x); // +의 의미는 문자열과 이어주겠다는 표식
					x++; //변수의 변화량
					
				}
		
		/* 변수를 생성
		 * 조건식에서 변수의 범위설정
		 * 반복할 명령
		 * 변수의 값을 변화시킬 수식 or 조건
		 */
		
		
		
		

	}

}
